eventScripts.script:
if not initialChange:
	path = "[.]New Tag"

	val = system.tag.readBlocking(path)[0].value

	system.tag.writeBlocking([path], val + 1)

	params = {}
	system.db.runNamedQuery("Shift/sp_PLAN_InsertDailyDefaultShifts", params)

if currentValue.value > previousValue.value:
	value = system.tag.readBlocking(["[.]Consecutive Scrap"])[0].value
	system.tag.writeBlocking(["[.]Consecutive Scrap"], [value + 1])
